axs[1].set_xlabel('X values')
axs[1].set_ylabel('Mean of a-values')
axs[1].set_title('Mean of a at each index with error bars')
axs[1].legend()
axs[1].grid(True)

# Show plots
plt.tight_layout()
plt.show()